        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tanantId + "/" + appId + "/" + featureId + "?subFeature=Choices_Items";
        var proxyMethod = "POST";
        var proxyData = {
            "categoryType": $rootScope.restCategoryItemSelect.categoryType,
            "itemChoice": $rootScope.restCategoryItemSelect.itemName
        };
        try {
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function (data, status, headers, config) {
                $.unblockUI();
                console.log(JSON.stringify(data));
                getQueryData(data);

            }).
            error(function (data, status, headers, config) {
                $.unblockUI();


            });

        }
        catch (e) {

        }

    }
    else {
        console.log("Choices not avaliable");
        $.unblockUI();
        console.log(JSON.stringify($rootScope.ChoicesArray));
        for (i = 0; i < countArray.length; i++) {
            countArray[i] = 0;
        }
        console.log("countInitialize " + countArray.length);

    }
    var counter = 0;

    function getQueryData(dataAll) {
        var dataLength = dataAll.length;
        if (counter < dataLength) {
            try {
                var currentObject = dataAll[counter];
                var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tanantId + "/" + appId + "/" + featureId + "?subFeature=Choices_ItemsSubTypes";
                var proxyMethod = "POST";
                var proxyData = {
                    "choiceType": currentObject.choiceName
                };
                $http({
                    method: proxyMethod,
                    url: url,
                    data: proxyData
                }).
                success(function (data, status, headers, config) {

                    try {
                        var choiceItemObject = {
                            "choiceName": currentObject.itemChoice,
                            "choiceDescription": data,
                            "minSelect": currentObject.minSelect,
                            "maxSelect": currentObject.maxSelect,
                            "choiceDesc": currentObject.choiceDesc,
                            "requiredDesc": currentObject.requiredDesc
                        };
                        $rootScope.ChoicesArray.push(choiceItemObject);
                        console.log(JSON.stringify($rootScope.ChoicesArray));
                        countArray = new Array($rootScope.ChoicesArray.length);

                        counter++;
                        getQueryData(dataAll);
                    }
                    catch (e) {
                        console.log(e);
                    }


                }).
                error(function (data, status, headers, config) {
                    $.unblockUI();
                });
            } catch (e) {

            }

        } else {
            countArray = new Array($rootScope.ChoicesArray.length);
            for (i = 0; i < countArray.length; i++) {
                countArray[i] = 0;
            }
            console.log("countInitialize " + countArray.length);
        }

    }

