            var id = '#' + i;
            $(id).prop("checked", false);

        }
        $rootScope.ChoicesArray[parentindex].choiceDescription[childindex].selected = true;
        console.log(JSON.stringify($rootScope.ChoicesArray[parentindex].choiceDescription));
        var id = '#' + childindex;
        $(childindex).prop("checked", true);
        countArray[parentindex] = 1;
        console.log(countArray);

    };


