        var min = today.getMinutes();
        var sec = today.getSeconds();
        var time = hh + ":" + min + ":" + sec;
        var dd = today.getDate();
        var mm = today.getMonth() + 1;//January is 0, so always add + 1

        var yyyy = today.getFullYear();
        if (dd < 10) {
            dd = '0' + dd
        }
        if (mm < 10) {
            mm = '0' + mm
        }
        today = yyyy + '-' + mm + '-' + dd;
        var postUrl = "https://dealmonk.in/restaurant_view_list_kryptos/";

        var postData = {

            "restaurant_id": restaurantId,
            "time": String(time),
            "date": today,
            "user_id": user_id, "offer_id": "999"
        };

        $http({
            url: postUrl,
            method: 'POST',
            data: postData
        }).then(function (res) {
            // alert(JSON.stringify(res.data.restaurantOfferList));

            $scope.liveoffer = res.data.restaurantOfferList[0];
            $scope.restOffer = $scope.liveoffer.restaurant_offer;
            $scope.name = res.data.restaurant_name;


            //display status only for live deal
            $scope.getCodeTextLive = res.data.restaurantOfferList[0].coupon_code;


            $scope.area = res.data.restaurant_area;
            $scope.image = res.data.restaurant_image;
            $scope.address1 = res.data.restaurant_address1;
            $scope.address2 = res.data.restaurant_address2;
            $scope.contact = res.data.restaurant_contact_detail;
            $scope.endTimeDeal = res.data.restaurantOfferList[0].end_time;
            try {
                if (res.data.restaurantOfferList.length > 1) {
                    $scope.upcomingoffer = res.data.restaurantOfferList[1];
                    $scope.hideGetCode = false;
                    $scope.getCodeTextUpcoming = res.data.restaurantOfferList[1].coupon_code;
                }
                else {
                    $scope.hideGetCode = true;

                }

            }
            catch (e) {

            }
            $scope.$apply();
            /*
             setInterval(timeRemaining, 1000);
             */
            $scope.km = $rootScope.km;
            $.unblockUI();
        }, function (err) {
            $scope.err = err;
            $.unblockUI();
        });

    }

    function timeRemaining() {

        var nowTime = new Date();
        var time = $scope.endTimeDeal;
        if (time.indexOf("PM"))
            var endTime = new Date(nowTime.getFullYear(), nowTime.getMonth(), nowTime.getDate(), parseInt(time.slice(0, 2)) + 12, 0, 0, 0);
        else
            var endTime = new Date(nowTime.getFullYear(), nowTime.getMonth(), nowTime.getDate(), parseInt(time.slice(0, 2)), 0, 0, 0);

        var diff = endTime.getTime() - nowTime.getTime();

        var second = (diff / 1000) % 60;

        var minutes = ((diff / (1000 * 60)) % 60);

        var hours = ((diff / (1000 * 60 * 60)) % 24);
        $scope.timeRemainingToEnd = Math.floor(hours) + "h:" + Math.floor(minutes) + "m:" + Math.floor(second) + "s";
        $scope.$apply();

    }

    $scope.openMap = function () {
        var url = "https://maps.google.com?saddr=" + $rootScope.userLat + "," + $rootScope.userLong + "&daddr=" + $rootScope.restLat + "," + $rootScope.restLong;

