            if ($scope.getCodeTextLive == "GET CODE") {
                confirmDeal(deal, flag);
                $.blockUI({
                    message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
                });
            }

        }
        else {
            //confirming an upcoming deal
            if ($scope.getCodeTextUpcoming.indexOf("GET CODE") != -1) {
                confirmDeal(deal, flag);
                $.blockUI({
                    message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
                });
            }
        }


    }
    $scope.setRestOffer = function (flag) {
        // alert(flag);
        if (flag) {
            $scope.restOffer = $scope.liveoffer.restaurant_offer;

        }
        else {
            try {
                $scope.restOffer = $scope.upcomingoffer.restaurant_offer;


            }
            catch (e) {

                $scope.restOffer = "No Upcoming Deal";
                $scope.hideGetCode = true;

            }

        }
        $scope.$apply();
    }
    function confirmDeal(deal, flag) {
        var today = new Date();
        var hh = today.getHours();
        var min = today.getMinutes();
        var sec = today.getSeconds();
        var time = hh + ":" + min + ":" + sec;
        var dd = today.getDate();
        var mm = today.getMonth() + 1;//January is 0, so always add + 1

        var yyyy = today.getFullYear();
        if (dd < 10) {
            dd = '0' + dd
        }
        if (mm < 10) {
            mm = '0' + mm
        }
        today = yyyy + '-' + mm + '-' + dd;


        //alert(deal.offer_id);
        var profiledata = $.jStorage.get("userdetails");

        var postUrl = "https://dealmonk.in/book_restaurant_kryptos/";
        var postData = {
            "mobile": profiledata.phone,
            "email": profiledata.email,
            "first_name": profiledata.firstname,
            "last_name": profiledata.lastname,
            //"user_id": "1654",
            "restaurantId": restaurantId,
            "offer_id": deal.offer_id,
            "covers": 1,
            "booking_time": String(time),
            "booking_date": today
        };
        $http({
            url: postUrl,
            method: 'POST',
            data: postData
        }).then(function (res) {
            if (res.data.success) {
                if (window.device) {
                    navigator.notification.alert("Cheers!" + "\n" + "Your Deal is Confirmed" + "\n" + res.data.coupon_code);
                }
                else {
                    alert("Cheers!" + "\n" + "Your Deal is Confirmed" + "\n" + res.data.coupon_code);
                }
                $rootScope.GetCode = true;
                if (flag) {
                    $scope.getCodeTextLive = res.data.coupon_code;

                }
                else {
                    $scope.getCodeTextUpcoming = res.data.coupon_code;
                }
                $rootScope.ShowDealDesc = true;
                $rootScope.toggle("replyThreadModal");

            }
            else {
                if (window.device) {
                    navigator.notification.alert(res.data.message);

                }
                else {
                    alert(res.data.message);
                }
            }
            $.unblockUI();
        }, function (err) {
            $scope.err = err;
            $.unblockUI();
        });

    }


}

