        if (isSuccess) {
          //store profile data in local storage
          $.jStorage.set("email", register_email);
          $.jStorage.set("password", register_password);
          //window.localStorage["email"] = register_email;
          //window.localStorage["password"] = register_password;
          var profileData = {
