function pageprocessorSettings2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    
    if($.jStorage.get("notificationAllowed") == null || $.jStorage.get("notificationAllowed") == undefined){
        $.jStorage.set("notificationAllowed",true);
        $scope.notification=true;
    }else{
        $scope.notification=$.jStorage.get("notificationAllowed");
    }
    if($.jStorage.get("locationAllowed") == null || $.jStorage.get("locationAllowed") == undefined){
        $.jStorage.set("locationAllowed",true);
        $scope.location=true;
    }else{
        $scope.location=$.jStorage.get("locationAllowed");
    }
    if($.jStorage.get("cameraAllowed") == null || $.jStorage.get("cameraAllowed") == undefined){
        $.jStorage.set("cameraAllowed",true);
        $scope.camera=true;
    }else{
        $scope.camera=$.jStorage.get("cameraAllowed");
    }
    
    $scope.logOut=function(){
        $scope.confirm=function(btn){
            if(btn == 1){
            $.jStorage.set("userdetails",null);
            $location.path("/app/SignIn2/SignIn2");
            $.jStorage.deleteKey("UserProfile");

            $.jStorage.deleteKey("profileData");
    }else{
    //$location.path("/home");
    }
    $scope.$apply();
    }
    navigator.notification.confirm("Are you sure you want to logout?",$scope.confirm,'Logout',['Yes','No']);
    }

    $scope.$watch('notification', function (newVal) {
        $.jStorage.set("notificationAllowed",$scope.notification);
        if($scope.notification == false){
        parsePlugin.unsubscribe('IIITD', function(msg) {
             }, function(e) {
             });
        }
    });

    $scope.$watch('location', function (newVal) {
        $.jStorage.set("locationAllowed",$scope.location);
    });

    $scope.$watch('camera', function (newVal) {
        $.jStorage.set("cameraAllowed",$scope.camera);
    });

    $scope.resetApp=function(btn){
        if(btn == 1){
            var tenant = MyCampusApp.config.tenant;
                $.jStorage.deleteKey(tenant + '-metadata');
                $location.path("/home");
                $scope.$apply();
        }
    }
    $scope.resetmyapp = function () {
        navigator.notification.confirm("Resetting your app clears all information associated with your app and initializes it with the default setting.  You can use this feature if your app is not functioning correctly. This feature requires network connectivity.",$scope.resetApp,'Reset App',['Yes','No']);
    }

    $scope.devicePage=function(){
        $location.path("/app/DevicePage/DevicePage");
    }
    $scope.changePassword=function(){
        $location.path("/app/ChangePassword/ChangePassword");   
    }

}

